// _items are enumerated backward
var _items  = ["Point 30P", "Discount 10%", "Back FREE", "Discount 30%", "Coupon 1+1", "Discount 20%", "Double x2", "Payback 100%"];
// _angles are enumberated forward
var _angles = [22.5, 67.5, 112.5, 157.5, 202.5, 247.5, 292.5, 337.5];

// Original Audio
//var snd = new Audio("data:audio/wav;base64,");
// Half Lengthed Audio
var _beepAudio    = new Audio("data:audio/wav;base64,");  
var _beepCount    = 10;
var _beepInterval = 1.1;
var _beepEnd      = false;

function beep(){
    try{
        _beepAudio.play();
        _beepCount *= _beepInterval;
        
        if(!_beepEnd){
            setTimeout("beep()", _beepCount);
        }
    }catch(x){
        console.log("beep() Exception : " + x);
    }
}

function init(){
    _beepCount = 10;
    _beepEnd   = false;
    _beepTimer = null;
}

function bet(){
    init();        	
    beep();
    
    $(".roulette_wheel").rotate({
            duration:5000,
            angle: 0,
            animateTo:getRandom(),
            callback: endBet
        }
    );
}

function getRandom(){
    /*
        return a number between 1500 and 5000
        */
    var result = Math.floor(Math.random() * (100 - 30) + 1) + 30;
    return result * 50;
}

function endBet(){
    _beepEnd = true;
    
    var _angle = $(".roulette_wheel").getRotateAngle();
    var angle = _angle % 360;
    var idx = 0;
    for(var i=0; i < _angles.length; i++){
        if(angle < _angles[i]){
            idx = i;
            break;
        }
    }
    
    setLastAlign(idx, angle);
    
    console.log(_angle + " : " + angle + " : " + idx + " : " + _items[idx]);
}

function setLastAlign(idx, angle){
    if(angle == 0) return;

    var leftAngle = _angles[idx] - ((idx == 0 && angle > _angles[_angles.length-1]) ? angle - 360 : angle);
    var halfClearance = (idx == 0 ? (_angles[idx] + (360 - _angles[_angles.length-1])) : (_angles[idx] - _angles[idx-1])) / 2;
    var toAngle = 0;
    var direction = "";
    if(leftAngle > halfClearance){
        direction = "Forward";
        toAngle = angle + leftAngle - halfClearance;
    }else if(leftAngle < halfClearance){
        direction = "Backward";
        toAngle = angle - (halfClearance - leftAngle);
    }else{
        return;
    }

    console.log("angle : " + angle + ", leftAngle : " + leftAngle +", halfClearance : " + halfClearance + ", direction : " + direction + ", toAngle : " + toAngle);
    
    _rotate(direction, angle, toAngle);
}

function _rotate(w, a, t){
    if(w == "Forward"){
        ++a;
    }else{
        --a;
    }
    
    console.log("_rotate : " + w + ", " + a + ", " + t);
    
    if(w == "Forward" && a >= t){
        return;
    }else if(w == "Backward" && a <= t){
        return;
    }
    
    $(".roulette_wheel").rotate(a);
    
    setTimeout("_rotate('"+w+"', "+a+", "+t+")", 50);
}